import {
  ColumnDef,
  ColumnFiltersState,
  ExpandedState,
  SortingState,
  VisibilityState,
  getCoreRowModel,
  getExpandedRowModel,
  getFacetedRowModel,
  getFacetedUniqueValues,
  getFilteredRowModel,
  getPaginationRowModel,
  getSortedRowModel,
  useReactTable,
  Row,
  PaginationState,
  Updater,
  TableState,
  TableOptions,
  RowSelectionState
} from '@tanstack/react-table'
import React from 'react'
import { parseAsInteger, parseAsString, useQueryState } from 'nuqs'
import { sortByToState, stateToSortBy } from '../lib/table-utils'

interface UseDataTableProps<TData extends { id: string }>
  extends Omit<
      TableOptions<TData>,
      'state' | 'pageCount' | 'getCoreRowModel' | 'manualFiltering' | 'manualPagination' | 'manualSorting'
    >,
    Required<Pick<TableOptions<TData>, 'pageCount'>> {
  columns: ColumnDef<TData>[]
  data: TData[]
  originalData?: TData[]
  createEmptyRow?: () => TData
  setData?: React.Dispatch<React.SetStateAction<TData[]>>
  updateRow?: (id: string, payload: TData) => void
  createRow?: (payload: TData) => void
  removeRow?: (id: string) => void
  getRowCanExpand?: (row: Row<TData>) => boolean
  onRemove?: (id: string) => void
  onUpdate?: (payload: TData) => void
  initialState?: Partial<TableState>
}

export function useDataTable<TData extends { id: string }>(props: UseDataTableProps<TData>) {
  const {
    columns,
    data,
    originalData,
    initialState,
    createEmptyRow,
    setData,
    updateRow,
    createRow,
    removeRow,
    getRowCanExpand,
    onRemove,
    onUpdate,
    pageCount = -1,
    ...tableProps
  } = props

  // * states
  const [rowSelection, setRowSelection] = React.useState<RowSelectionState>(initialState?.rowSelection ?? {})
  const [columnVisibility, setColumnVisibility] = React.useState<VisibilityState>(initialState?.columnVisibility ?? {})
  const [columnFilters, setColumnFilters] = React.useState<ColumnFiltersState>([])

  const [editedRows, setEditedRows] = React.useState<Record<string, boolean>>({})
  const [validRows, setValidRows] = React.useState<Record<string, Record<string, boolean>>>({})
  const [pendingCreate, setPendingCreate] = React.useState<{
    data: TData
    index: number
  } | null>(null)
  const [expanded, setExpanded] = React.useState<ExpandedState>({})

  const [page, setPage] = useQueryState('page', parseAsInteger.withDefault(1).withOptions({ shallow: false }))
  const [perPage, setPerPage] = useQueryState(
    'perPage',
    parseAsInteger
      .withDefault(10)
      .withOptions({ shallow: false })
      .withDefault(initialState?.pagination?.pageSize ?? 10)
  )

  const [globalFilter, setGlobalFilter] = useQueryState(
    'search',
    parseAsString.withDefault('').withOptions({ shallow: false })
  )

  // * helper functions
  const initializeValidationState = () => {
    const validationState: Record<string, boolean> = {}
    columns.forEach((column: any) => {
      if (column.meta?.zod) {
        validationState[column.accessorKey as string] = false
      }
    })
    return validationState
  }

  // * row actions
  const handleOnRemove = (id: string) => {
    onRemove!(id)
  }

  const handleUpdate = (payload: TData) => {
    onUpdate!(payload)
  }

  // * paginate
  const pagination: PaginationState = {
    pageIndex: page - 1, // zero-based index -> one-based index
    pageSize: perPage
  }

  function onPaginationChange(updaterOrValue: Updater<PaginationState>) {
    if (typeof updaterOrValue === 'function') {
      const newPagination = updaterOrValue(pagination)
      void setPage(newPagination.pageIndex + 1)
      void setPerPage(newPagination.pageSize)
    } else {
      void setPage(updaterOrValue.pageIndex + 1)
      void setPerPage(updaterOrValue.pageSize)
    }
  }

  const [sorting, setSorting] = useQueryState(
    'sort',
    parseAsString.withDefault(stateToSortBy(initialState?.sorting) ?? '').withOptions({ shallow: false })
  )
  const sortingState = sortByToState(sorting)

  const onSortingChange = React.useCallback(
    (updaterOrValue: Updater<SortingState>) => {
      const newSortingState = typeof updaterOrValue === 'function' ? updaterOrValue(sortingState) : updaterOrValue

      // Pastikan kita tidak pernah melewatkan undefined ke setSorting
      const newSortValue = stateToSortBy(newSortingState)
      return setSorting(newSortValue)
    },
    [sorting, setSorting]
  )

  // * editable functions
  const handleRevertData = (rowIndex: number) => {
    if (pendingCreate?.index === rowIndex) {
      setData!((old) => old.filter((_, index) => index !== rowIndex))
      setPendingCreate(null)
      setValidRows((old) => {
        const newValidRows = { ...old }
        delete newValidRows[rowIndex]
        return newValidRows
      })
    } else {
      setData!((old) => old.map((row, index) => (index === rowIndex ? originalData![rowIndex]! : row)))
    }
  }

  const handleUpdateRow = (rowIndex: number) => {
    if (pendingCreate?.index === rowIndex) {
      createRow!(data[rowIndex]!)
      setPendingCreate(null)
    } else {
      updateRow!(data[rowIndex]!.id, data[rowIndex]!)
    }
  }

  const handleUpdateData = (rowIndex: number, columnId: string, value: TData, isValid: boolean) => {
    setData!((old: TData[]) =>
      old.map((row, index) => {
        if (index === rowIndex) {
          return {
            ...old[rowIndex]!,
            [columnId]: value
          }
        }
        return row
      })
    )
    setValidRows((old) => ({
      ...old,
      [rowIndex]: { ...old[rowIndex], [columnId]: isValid }
    }))
  }

  const handleCreateRow = () => {
    const newRow = createEmptyRow!()
    setData!((old) => [newRow, ...old])

    setPendingCreate({
      data: newRow,
      index: 0
    })

    setEditedRows((old) => ({
      ...old,
      0: true
    }))

    const initialValidation = initializeValidationState()
    setValidRows((old) => ({
      ...old,
      0: initialValidation
    }))
  }

  const handleRemoveRow = (rowIndex: number) => {
    if (pendingCreate?.index === rowIndex) {
      setData!((old) => old.filter((_, index) => index !== rowIndex))
      setPendingCreate(null)
      setValidRows((old) => {
        const newValidRows = { ...old }
        delete newValidRows[rowIndex]
        return newValidRows
      })
    } else {
      removeRow!(data[rowIndex]!.id)
    }
  }

  const handleRemoveSelectedRows = (selectedRows: number[]) => {
    selectedRows.forEach((rowIndex) => {
      if (pendingCreate?.index !== rowIndex) {
        removeRow!(data[rowIndex]!.id)
      }
    })
  }

  /**
   ** Table Configuration
   */
  const table = useReactTable({
    ...tableProps,
    initialState,
    data,
    columns,
    pageCount,
    state: {
      pagination,
      sorting: sortingState,
      columnVisibility,
      rowSelection,
      columnFilters,
      expanded,
      globalFilter
    },
    columnResizeMode: 'onChange',
    globalFilterFn: 'includesString',
    enableRowSelection: true,
    onPaginationChange,
    onRowSelectionChange: setRowSelection,
    onSortingChange,
    onGlobalFilterChange: setGlobalFilter,
    onColumnFiltersChange: setColumnFilters,
    onColumnVisibilityChange: setColumnVisibility,
    getCoreRowModel: getCoreRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFacetedRowModel: getFacetedRowModel(),
    getFacetedUniqueValues: getFacetedUniqueValues(),
    onExpandedChange: setExpanded,
    getExpandedRowModel: getExpandedRowModel(),
    getRowCanExpand,
    meta: {
      editedRows,
      setEditedRows,
      validRows,
      setValidRows,
      pendingCreate,
      setPendingCreate,
      onRemove: handleOnRemove,
      onUpdate: handleUpdate,
      revertData: handleRevertData,
      updateRow: handleUpdateRow,
      updateData: handleUpdateData,
      createRow: handleCreateRow,
      removeRow: handleRemoveRow,
      removeSelectedRows: handleRemoveSelectedRows
    },
    manualPagination: true,
    manualFiltering: true,
    manualSorting: true
  })

  return { table }
}

import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
export interface Student {
  id: string;
  studentNumber: string;
  name: string;
  dateOfBirth: string;
  major: string;
  createdAt: string;
}

const url = "http://localhost:5000/students";

async function updateRequest(id: string, data: Student) {
  const response = await fetch(`${url}/${id}`, {
    method: "PUT",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(data),
  });
  return response.json();
}

async function addRequest(data: Student) {
  const response = await fetch(url, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(data),
  });
  return response.json();
}

async function deleteRequest(id: string) {
  const response = await fetch(`${url}/${id}`, {
    method: "DELETE",
    headers: {
      "Content-Type": "application/json",
    },
  });
  return response.json();
}

async function getRequest() {
  const response = await fetch(url);
  const data = await response.json();

  // Mengurutkan data secara descending berdasarkan createdAt
  const sortedData = data.sort(
    (a: Student, b: Student) =>
      new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
  );

  return sortedData;
}

export default function useStudents() {
  const queryClient = useQueryClient();

  // Query for fetching students
  const { data, isLoading } = useQuery({
    queryKey: ["students"],
    queryFn: getRequest,
    initialData: [],
  });

  // Mutation for updating a student
  const updateMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: Student }) =>
      updateRequest(id, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["students"] });
    },
  });

  // Mutation for deleting a student
  const deleteMutation = useMutation({
    mutationFn: (id: string) => deleteRequest(id),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["students"] });
    },
  });

  // Mutation for adding a student
  const addMutation = useMutation({
    mutationFn: (data: Student) => addRequest(data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["students"] });
    },
  });

  return {
    data: data ?? [],
    isLoading,
    createRow: (postData: Student) => addMutation.mutate(postData),
    updateRow: (id: string, postData: Student) =>
      updateMutation.mutate({ id, data: postData }),
    removeRow: (id: string) => deleteMutation.mutate(id),
  };
}

import { createSearchParamsCache, parseAsInteger, parseAsString } from 'nuqs/server'
import { z, ZodType } from 'zod'

export const searchParamsCache = createSearchParamsCache({
  page: parseAsInteger.withDefault(1),
  perPage: parseAsInteger.withDefault(10),
  search: parseAsString.withDefault(''),
  sort: parseAsString.withDefault('')
})

export type QuerySchema = Awaited<ReturnType<typeof searchParamsCache.parse>>

export class UserSchema {
  static readonly CREATE: ZodType = z.object({
    name: z.string(),
    email: z.string().email(),
    phone: z.string(),
    image: z.string()
  })

  static readonly UPDATE: ZodType = z.object({
    id: z.string(),
    name: z.string().optional(),
    email: z.string().email().optional(),
    phone: z.string().optional(),
    image: z.string().optional()
  })
}

export const searchParams = z.object({
  page: z.number().default(1),
  perPage: z.number().max(50).default(10),
  search: z.string().optional(),
  sort: z.string().optional()
})

import "@tanstack/react-table";
import { ColumnDef, Row, RowData } from "@tanstack/react-table";
import { ZodType, ZodTypeDef } from "zod";

declare module "@tanstack/react-table" {
  interface ColumnMeta<TData extends RowData, TValue> {
    disabled?: boolean | ((row: Row<TData>) => boolean)
    type?: 'text' | 'number' | 'date' | 'select' | 'combobox' | 'password'
    schema?: ZodType<any, ZodTypeDef, any>,
    options?: { label: string; value: string }[]
  }

  interface TableMeta<TData extends RowData> {
    editedRows?: Record<string, boolean>;
    setEditedRows?: React.Dispatch<
      React.SetStateAction<Record<string, boolean>>
    >;
    validRows?: Record<string, Record<string, boolean>>;
    setValidRows?: React.Dispatch<
      React.SetStateAction<Record<string, Record<string, boolean>>>
    >;
    rowOriginal?: TData | undefined;
    setRowOriginal?: React.Dispatch<React.SetStateAction<TData | undefined>>;
    revertData?: (rowIndex: number) => void;
    updateRow?: (rowIndex: number) => void;
    updateData?: (
      rowIndex: number,
      columnId: string,
      value: TData,
      isValid: boolean
    ) => void;
    createRow?: () => void;
    removeRow?: (rowIndex: number) => void;
    removeSelectedRows?: (selectedRows: number[]) => void;
    onRemove?: (id: string) => void;
    onUpdate?: (payload: TData) => void;
    pendingCreate?: PendingCreate<TData> | null;
    setPendingCreate?: React.Dispatch<
      React.SetStateAction<PendingCreate<TData> | null>
    >;
  }
}

export type Option = {
  label: string;
  value: string;
};

import { ColumnDef } from '@tanstack/react-table'
import { DataTableColumnHeader } from '@/registry/blocks/server-table/block/data-table-column-header'
import { EditedCell } from './edit-table-row-action'
import { EditTableCell } from '@/registry/blocks/server-table/block/edit-table-cell'
import { Checkbox } from '@/components/ui/checkbox'
import { Student } from '@/registry/blocks/server-table/hooks/use-querry'
import { z } from 'zod'

export const columns: ColumnDef<Student>[] = [
  {
    id: 'select',
    header: ({ table }) => (
      <Checkbox
        checked={table.getIsAllPageRowsSelected() || (table.getIsSomePageRowsSelected() && 'indeterminate')}
        onCheckedChange={(value) => table.toggleAllPageRowsSelected(!!value)}
        aria-label="Select all"
        className="translate-y-[2px]"
      />
    ),
    cell: ({ row }) => (
      <Checkbox
        checked={row.getIsSelected()}
        onCheckedChange={(value) => row.toggleSelected(!!value)}
        aria-label="Select row"
        className="translate-y-[2px]"
      />
    ),
    enableSorting: false,
    enableHiding: false
  },
  {
    accessorKey: 'no',
    header: ({ column }) => <DataTableColumnHeader column={column} title="No" />,
    cell: ({ row }) => <div className="w-[80px]">{row.index + 1}</div>,
    enableSorting: false,
    enableHiding: false
  },
  {
    accessorKey: 'name',
    header: ({ column }) => <DataTableColumnHeader column={column} title="Full Name" />,
    cell: ({ column, getValue, row, table }) => (
      <EditTableCell column={column} getValue={getValue} row={row} table={table} />
    ),
    meta: {
      type: 'text',
      schema: z.string().min(1, { message: 'Name is required boss' })
    }
  },
  {
    accessorKey: 'dateOfBirth',
    header: ({ column }) => <DataTableColumnHeader column={column} title="Date Of Birth" />,
    cell: ({ column, getValue, row, table }) => (
      <EditTableCell column={column} getValue={getValue} row={row} table={table} />
    ),
    meta: {
      type: 'date',
    }
  },
  {
    accessorKey: 'major',
    header: ({ column }) => <DataTableColumnHeader column={column} title="Major" />,
    cell: ({ column, getValue, row, table }) => (
      <EditTableCell column={column} getValue={getValue} row={row} table={table} />
    ),
    meta: {
      type: 'select',
      options: [
        { value: 'Computer Science', label: 'Computer Science' },
        { value: 'Communications', label: 'Communications' },
        { value: 'Business', label: 'Business' },
        { value: 'Psychology', label: 'Psychology' }
      ],
    }
  },
  {
    id: 'actions',
    cell: ({ row, table }) => <EditedCell title={row.getValue('name')} row={row} table={table} />
  }
]

'use client'

import type { Column } from '@tanstack/react-table'
import { ChevronDown, ChevronUp, ChevronsUpDown, EyeOff, X } from 'lucide-react'

import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger
} from '@/components/ui/dropdown-menu'
import { cn } from '@/lib/utils'

interface DataTableColumnHeaderProps<TData, TValue> extends React.ComponentProps<typeof DropdownMenuTrigger> {
  column: Column<TData, TValue>
  title: string
}

export function DataTableColumnHeader<TData, TValue>({
  column,
  title,
  className,
  ...props
}: DataTableColumnHeaderProps<TData, TValue>) {
  if (!column.getCanSort() && !column.getCanHide()) {
    return <div className={cn(className)}>{title}</div>
  }

  return (
    <DropdownMenu>
      <DropdownMenuTrigger
        className={cn(
          '-ml-1.5 flex h-8 items-center gap-1.5 rounded-sm px-2 py-1.5 hover:bg-accent focus:outline-none focus:ring-1 focus:ring-ring data-[state=open]:bg-accent [&_svg]:size-4 [&_svg]:shrink-0 [&_svg]:text-muted-foreground',
          className
        )}
        {...props}
      >
        {title}
        {column.getCanSort() &&
          (column.getIsSorted() === 'desc' ? (
            <ChevronDown />
          ) : column.getIsSorted() === 'asc' ? (
            <ChevronUp />
          ) : (
            <ChevronsUpDown />
          ))}
      </DropdownMenuTrigger>
      <DropdownMenuContent  align="start" side='right' className="min-w-auto">
        {column.getCanSort() && (
          <>
            <DropdownMenuCheckboxItem
              className="relative pr-8 pl-2 [&>span:first-child]:right-2 [&>span:first-child]:left-auto [&_svg]:text-muted-foreground"
              checked={column.getIsSorted() === 'asc'}
              onClick={() => column.toggleSorting(false)}
            >
              <ChevronUp />
              Asc
            </DropdownMenuCheckboxItem>
            <DropdownMenuCheckboxItem
              className="relative pr-8 pl-2 [&>span:first-child]:right-2 [&>span:first-child]:left-auto [&_svg]:text-muted-foreground"
              checked={column.getIsSorted() === 'desc'}
              onClick={() => column.toggleSorting(true)}
            >
              <ChevronDown />
              Desc
            </DropdownMenuCheckboxItem>
            {column.getIsSorted() && (
              <DropdownMenuItem className="pl-2 [&_svg]:text-muted-foreground" onClick={() => column.clearSorting()}>
                <X />
                Reset
              </DropdownMenuItem>
            )}
          </>
        )}
        {column.getCanHide() && (
          <DropdownMenuCheckboxItem
            className="relative pr-8 pl-2 [&>span:first-child]:right-2 [&>span:first-child]:left-auto [&_svg]:text-muted-foreground"
            checked={!column.getIsVisible()}
            onClick={() => column.toggleVisibility(false)}
          >
            <EyeOff />
            Hide
          </DropdownMenuCheckboxItem>
        )}
      </DropdownMenuContent>
    </DropdownMenu>
  )
}

import * as React from "react";
import { Column } from "@tanstack/react-table";
import { Check, PlusCircle } from "lucide-react";

import { cn } from "@/lib/utils";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
  CommandSeparator,
} from "@/components/ui/command";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { Separator } from "@/components/ui/separator";

interface DataTableFacetedFilterProps<TData, TValue> {
  column?: Column<TData, TValue>;
  title?: string;
  options: {
    label: string;
    value: string;
    icon?: React.ComponentType<{ className?: string }>;
  }[];
}

export function DataTableFacetedFilter<TData, TValue>({
  column,
  title,
  options,
}: DataTableFacetedFilterProps<TData, TValue>) {
  const facets = column?.getFacetedUniqueValues();
  const selectedValues = new Set(column?.getFilterValue() as string[]);

  return (
    <Popover>
      <PopoverTrigger asChild>
        <Button variant="outline" size="sm" className="h-8 border-dashed">
          <PlusCircle />
          {title}
          {selectedValues?.size > 0 && (
            <>
              <Separator orientation="vertical" className="mx-2 h-4" />
              <Badge
                variant="secondary"
                className="rounded-sm px-1 font-normal lg:hidden"
              >
                {selectedValues.size}
              </Badge>
              <div className="hidden space-x-1 lg:flex">
                {selectedValues.size > 2 ? (
                  <Badge
                    variant="secondary"
                    className="rounded-sm px-1 font-normal"
                  >
                    {selectedValues.size} selected
                  </Badge>
                ) : (
                  options
                    .filter((option) => selectedValues.has(option.value))
                    .map((option) => (
                      <Badge
                        variant="secondary"
                        key={option.value}
                        className="rounded-sm px-1 font-normal"
                      >
                        {option.label}
                      </Badge>
                    ))
                )}
              </div>
            </>
          )}
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-[200px] p-0" align="start">
        <Command>
          <CommandInput placeholder={title} />
          <CommandList>
            <CommandEmpty>No results found.</CommandEmpty>
            <CommandGroup>
              {options.map((option) => {
                const isSelected = selectedValues.has(option.value);
                return (
                  <CommandItem
                    key={option.value}
                    onSelect={() => {
                      if (isSelected) {
                        selectedValues.delete(option.value);
                      } else {
                        selectedValues.add(option.value);
                      }
                      const filterValues = Array.from(selectedValues);
                      column?.setFilterValue(
                        filterValues.length ? filterValues : undefined
                      );
                    }}
                  >
                    <div
                      className={cn(
                        "mr-2 flex h-4 w-4 items-center justify-center rounded-sm border border-primary",
                        isSelected
                          ? "bg-primary text-primary-foreground"
                          : "opacity-50 [&_svg]:invisible"
                      )}
                    >
                      <Check />
                    </div>
                    {option.icon && (
                      <option.icon className="mr-2 h-4 w-4 text-muted-foreground" />
                    )}
                    <span>{option.label}</span>
                    {facets?.get(option.value) && (
                      <span className="ml-auto flex h-4 w-4 items-center justify-center font-mono text-xs">
                        {facets.get(option.value)}
                      </span>
                    )}
                  </CommandItem>
                );
              })}
            </CommandGroup>
            {selectedValues.size > 0 && (
              <>
                <CommandSeparator />
                <CommandGroup>
                  <CommandItem
                    onSelect={() => column?.setFilterValue(undefined)}
                    className="justify-center text-center"
                  >
                    Clear filters
                  </CommandItem>
                </CommandGroup>
              </>
            )}
          </CommandList>
        </Command>
      </PopoverContent>
    </Popover>
  );
}

import { Table } from "@tanstack/react-table";
import {
  ChevronLeft,
  ChevronRight,
  ChevronsLeft,
  ChevronsRight,
} from "lucide-react";

import { Button } from "@/components/ui/button";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

interface DataTablePaginationProps<TData> {
  table: Table<TData>;
  pageSizeOptions?: number[];
}

export function DataTablePagination<TData>({
  table,
  pageSizeOptions = [10, 20, 30, 40, 50],
}: DataTablePaginationProps<TData>) {
  return (
    <div className="flex items-center justify-between px-2">
      <div className="flex-1 text-sm text-muted-foreground">
        {table.getFilteredSelectedRowModel().rows.length} of {table.getFilteredRowModel().rows.length} row(s) selected.
      </div>
      <div className="flex items-center space-x-6 lg:space-x-8">
        <div className="flex items-center space-x-2">
          <p className="text-sm font-medium">Rows per page</p>
          <Select
            value={`${table.getState().pagination.pageSize}`}
            onValueChange={(value) => {
              table.setPageSize(Number(value))
            }}
          >
            <SelectTrigger size="sm" className="w-[70px]">
              <SelectValue placeholder={table.getState().pagination.pageSize} />
            </SelectTrigger>
            <SelectContent side="top" className="min-w-[70px]">
              {pageSizeOptions.map((pageSize) => (
                <SelectItem key={pageSize} value={`${pageSize}`}>
                  {pageSize}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
        <div className="flex w-[100px] items-center justify-center text-sm font-medium">
          Page {table.getState().pagination.pageIndex + 1} of {table.getPageCount()}
        </div>
        <div className="flex items-center space-x-2">
          <Button
            variant="outline"
            className="hidden h-8 w-8 p-0 lg:flex"
            onClick={() => table.setPageIndex(0)}
            disabled={!table.getCanPreviousPage()}
          >
            <span className="sr-only">Go to first page</span>
            <ChevronsLeft />
          </Button>
          <Button
            variant="outline"
            className="h-8 w-8 p-0"
            onClick={() => table.previousPage()}
            disabled={!table.getCanPreviousPage()}
          >
            <span className="sr-only">Go to previous page</span>
            <ChevronLeft />
          </Button>
          <Button
            variant="outline"
            className="h-8 w-8 p-0"
            onClick={() => table.nextPage()}
            disabled={!table.getCanNextPage()}
          >
            <span className="sr-only">Go to next page</span>
            <ChevronRight />
          </Button>
          <Button
            variant="outline"
            className="hidden h-8 w-8 p-0 lg:flex"
            onClick={() => table.setPageIndex(table.getPageCount() - 1)}
            disabled={!table.getCanNextPage()}
          >
            <span className="sr-only">Go to last page</span>
            <ChevronsRight />
          </Button>
        </div>
      </div>
    </div>
  )
}

'use client'

import { Table } from '@tanstack/react-table'
import { X } from 'lucide-react'

import { Button } from '@/components/ui/button'
import { DataTableViewOptions } from './data-table-view-options'
import { DataTableFacetedFilter } from '@/registry/blocks/server-table/block/data-table-faceted-filter'
import { cn } from '@/lib/utils'
import { DebouncedInput } from '@/registry/ui/debounce-input'

interface DataTableToolbarProps<TData> extends React.HTMLAttributes<HTMLDivElement> {
  table: Table<TData>
  facetedFilters?: {
    column: string
    title: string
    options: {
      label: string
      value: string
      icon?: React.ComponentType<{ className?: string }>
    }[]
  }[]
}
export function DataTableToolbar<TData>({
  table,
  facetedFilters,
  children,
  className,
  ...props
}: DataTableToolbarProps<TData>) {
  const isFiltered = table.getState().columnFilters.length > 0
  const meta = table.options.meta
  const selectedRows = table.getSelectedRowModel().rows

  const removeRows = () => {
    meta?.removeSelectedRows!(table.getSelectedRowModel().rows.map((row) => row.index))
    table.resetRowSelection()
  }
  const global = table.getState().globalFilter

  return (
    <div className={cn('flex w-full items-center justify-between gap-2 overflow-auto p-1', className)} {...props}>
      <div className="flex flex-1 items-center space-x-2">
        <DebouncedInput
          className="-ms-1 h-8 w-[150px] lg:w-[250px]"
          value={global}
          placeholder="Search..."
          onChange={(value) => table.setGlobalFilter(String(value).trim())}
        />
        {facetedFilters?.map((filter) => (
          <DataTableFacetedFilter
            key={filter.column}
            column={table.getColumn(filter.column)}
            title={filter.title}
            options={filter.options}
          />
        ))}
        {isFiltered && (
          <Button variant="ghost" onClick={() => table.resetColumnFilters()} className="h-8 px-2 lg:px-3">
            Reset
            <X />
          </Button>
        )}
        {selectedRows.length > 0 && (
          <Button variant="destructive" size={'sm'} onClick={removeRows}>
            {`Delete ${selectedRows.length} Selected`}
          </Button>
        )}
      </div>
      <div className="flex items-center space-x-2">
        {children}
        <DataTableViewOptions table={table} />
      </div>
    </div>
  )
}

"use client";

import { Table } from "@tanstack/react-table";
import { Settings2 } from "lucide-react";

import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

interface DataTableViewOptionsProps<TData> {
  table: Table<TData>;
}

export function DataTableViewOptions<TData>({
  table,
}: DataTableViewOptionsProps<TData>) {
  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button
          variant="outline"
          size="sm"
          className="ml-auto hidden h-8 lg:flex"
        >
          <Settings2 />
          View
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end" className="w-[150px]">
        <DropdownMenuLabel>Toggle columns</DropdownMenuLabel>
        <DropdownMenuSeparator />
        {table
          .getAllColumns()
          .filter(
            (column) =>
              typeof column.accessorFn !== "undefined" && column.getCanHide()
          )
          .map((column) => {
            return (
              <DropdownMenuCheckboxItem
                key={column.id}
                className="capitalize"
                checked={column.getIsVisible()}
