import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table'
import { type Table as TanstackTable, Column, flexRender, Row } from '@tanstack/react-table'
import React, { CSSProperties } from 'react'
import { DataTablePagination } from './data-table-pagination'
import { cn } from '@/lib/utils'
import { Button } from '@/components/ui/button'
import { ArrowLeftToLineIcon, ArrowRightToLineIcon, EllipsisIcon, PinOffIcon } from 'lucide-react'
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from '@/components/ui/dropdown-menu'

export interface DataTableProps<TData> extends React.HTMLAttributes<HTMLDivElement> {
  /**
   * The table instance returned from useDataTable hook with pagination, sorting, filtering, etc.
   * @type TanstackTable<TData>
   */
  table: TanstackTable<TData>
  /**
   * Callback for handling row clicks.
   * @param row - The row data that was clicked.
   */
  onRowClick?: (row: TData) => void

  /**
   * Function to render a sub-component for expandable rows.
   * @param props - The row properties.
   * @returns A React element.
   */
  renderSubComponent?: (props: { row: Row<TData> }) => React.ReactElement

  /**
   * Whether to show pagination controls.
   * @default true
   */
  showPagination?: boolean
}

export function DataTable<TData>({
  table,
  onRowClick,
  renderSubComponent,
  showPagination = true,
  children,
  className,
  ...props
}: DataTableProps<TData>) {
  // Helper function to compute pinning styles for columns
  const getPinningStyles = (column: Column<TData>): CSSProperties => {
    const isPinned = column.getIsPinned()
    return {
      left: isPinned === 'left' ? `${column.getStart('left')}px` : undefined,
      right: isPinned === 'right' ? `${column.getAfter('right')}px` : undefined,
      position: isPinned ? 'sticky' : 'relative',
      width: column.getSize(),
      zIndex: isPinned ? 1 : 0
    }
  }

  return (
    <div className={cn('w-full space-y-2.5 overflow-auto', className)} {...props}>
      {children}
      <div className="rounded-md border">
        <Table
          className="[&_td]:border-border [&_th]:border-border table-fixed border-separate border-spacing-0 [&_tfoot_td]:border-t [&_th]:border-b [&_tr]:border-none [&_tr:not(:last-child)_td]:border-b"
        >
          <TableHeader>
            {table.getHeaderGroups().map((headerGroup) => (
              <TableRow key={headerGroup.id} className="bg-muted/50">
                {headerGroup.headers.map((header) => {
                  const { column } = header
                  const isPinned = column.getIsPinned()
                  const isLastLeftPinned = isPinned === 'left' && column.getIsLastColumn('left')
                  const isFirstRightPinned = isPinned === 'right' && column.getIsFirstColumn('right')

                  return (
                    <TableHead
                      key={header.id}
                      colSpan={header.colSpan}
                      className="[&[data-pinned][data-last-col]]:border-border data-pinned:bg-muted/90 relative h-10 truncate  data-pinned:backdrop-blur-xs [&:not([data-pinned]):has(+[data-pinned])_div.cursor-col-resize:last-child]:opacity-0 [&[data-last-col=left]_div.cursor-col-resize:last-child]:opacity-0 [&[data-pinned=left][data-last-col=left]]:border-r [&[data-pinned=right]:last-child_div.cursor-col-resize:last-child]:opacity-0 [&[data-pinned=right][data-last-col=right]]:border-l"
                      style={{ ...getPinningStyles(column) }}
                      data-pinned={isPinned || undefined}
                      data-last-col={isLastLeftPinned ? 'left' : isFirstRightPinned ? 'right' : undefined}
                    >
                      <div className="flex items-center justify-between gap-2">
                        <span>
                          {header.isPlaceholder
                            ? null
                            : flexRender(header.column.columnDef.header, header.getContext())}
                        </span>
                        {/* Pin/Unpin column controls with enhanced accessibility */}
                        {!header.isPlaceholder &&
                          header.column.getCanPin() &&
                          (header.column.getIsPinned() ? (
                            <Button
                              size="icon"
                              variant="ghost"
                              className="-mr-1 size-7 shadow-none"
                              onClick={() => header.column.pin(false)}
                              aria-label={`Unpin ${header.column.id as string} column`}
                              title={`Unpin ${header.column.id as string} column`}
                            >
                              <PinOffIcon className="opacity-60" size={16} aria-hidden="true" />
                            </Button>
                          ) : (
                            <DropdownMenu>
                              <DropdownMenuTrigger asChild>
                                <Button
                                  size="icon"
                                  variant="ghost"
                                  className="-mr-1 size-7 shadow-none"
                                  aria-label={`Pin options for ${header.column.id as string} column`}
                                  title={`Pin options for ${header.column.id as string} column`}
                                >
                                  <EllipsisIcon className="opacity-60" size={16} aria-hidden="true" />
                                </Button>
                              </DropdownMenuTrigger>
                              <DropdownMenuContent align="end">
                                <DropdownMenuItem onClick={() => header.column.pin('left')}>
                                  <ArrowLeftToLineIcon size={16} className="opacity-60" aria-hidden="true" />
                                  Stick to left
                                </DropdownMenuItem>
                                <DropdownMenuItem onClick={() => header.column.pin('right')}>
                                  <ArrowRightToLineIcon size={16} className="opacity-60" aria-hidden="true" />
                                  Stick to right
                                </DropdownMenuItem>
                              </DropdownMenuContent>
                            </DropdownMenu>
                          ))}
                        {header.column.getCanResize() && (
                          <div
                            {...{
                              onDoubleClick: () => header.column.resetSize(),
                              onMouseDown: header.getResizeHandler(),
                              onTouchStart: header.getResizeHandler(),
                              className:
                                'absolute top-0 h-full w-4 cursor-col-resize user-select-none touch-none -right-2 z-10 flex justify-center before:absolute before:w-px before:inset-y-0 before:bg-border before:-translate-x-px'
                            }}
                          />
                        )}
                      </div>
                    </TableHead>
                  )
                })}
              </TableRow>
            ))}
          </TableHeader>
          <TableBody>
            {table.getRowModel().rows?.length ? (
              table.getRowModel().rows.map((row) => (
                <React.Fragment key={`fragment-${row.id}`}>
                  <TableRow
                    className={onRowClick ? 'cursor-pointer' : ''}
                    onClick={() => onRowClick && onRowClick(row.original)}
                    data-state={row.getIsSelected() && 'selected'}
                  >
                    {row.getVisibleCells().map((cell) => {
                      const { column } = cell
                      const isPinned = column.getIsPinned()
                      const isLastLeftPinned = isPinned === 'left' && column.getIsLastColumn('left')
                      const isFirstRightPinned = isPinned === 'right' && column.getIsFirstColumn('right')
                      return (
                        <TableCell
                          key={cell.id}
                          className="[&[data-pinned][data-last-col]]:border-border data-pinned:bg-background/90 truncate data-pinned:backdrop-blur-xs [&[data-pinned=left][data-last-col=left]]:border-r [&[data-pinned=right][data-last-col=right]]:border-l"
                          style={{ ...getPinningStyles(column) }}
                          data-pinned={isPinned || undefined}
                          data-last-col={isLastLeftPinned ? 'left' : isFirstRightPinned ? 'right' : undefined}
                        >
                          {flexRender(cell.column.columnDef.cell, cell.getContext())}
                        </TableCell>
                      )
                    })}
                  </TableRow>
                  {row.getIsExpanded() && renderSubComponent && (
                    <TableRow>
                      <TableCell colSpan={row.getVisibleCells().length}>{renderSubComponent({ row })}</TableCell>
                    </TableRow>
                  )}
                </React.Fragment>
              ))
            ) : (
              <TableRow>
                <TableCell colSpan={table.getAllColumns().length} className="h-24 text-center">
                  No results.
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>
      {showPagination && <DataTablePagination table={table} />}
    </div>
  )
}

import { Input } from '@/registry/ui/input'
import { Select, SelectContent, SelectGroup, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { cn } from '@/lib/utils'
import { Column, Row, Table } from '@tanstack/react-table'
import { useState, useEffect, ChangeEvent } from 'react'
import { Option } from '@/registry/blocks/server-table/lib/types'
import { ZodError } from 'zod'

type EditTableCellProps<TData> = {
  getValue: () => any
  row: Row<TData>
  column: Column<TData>
  table: Table<TData>
}

export function EditTableCell<TData>({ getValue, row, column, table }: EditTableCellProps<TData>) {
  const initValue = getValue()
  const columnMeta = column.columnDef.meta
  const tableMeta = table.options.meta
  const [value, setValue] = useState(initValue)
  const [valMsg, setvalMsg] = useState('')

  if (!tableMeta || !columnMeta || !tableMeta.editedRows) {
    throw new Error('Table meta or column meta is not defined')
  }

  useEffect(() => {
    if (tableMeta.editedRows && tableMeta.editedRows[row.id]) {
      validateInput(initValue)
    }
  }, [tableMeta.editedRows[row.id]])

  useEffect(() => {
    setValue(initValue)
    if (tableMeta.editedRows && tableMeta.editedRows[row.id]) {
      validateInput(initValue)
    }
  }, [initValue])

  const validateInput = (inputValue: any) => {
    let message = ''
    let isValid = true

    // Zod validation
    if (columnMeta.schema) {
      try {
        columnMeta.schema.parse(inputValue)
      } catch (error) {
        if (error instanceof ZodError) {
          message = error.errors[0]?.message ?? 'Invalid input'
          isValid = false
        }
      }
    }

    setvalMsg(message)
    tableMeta.updateData!(row.index, column.id, inputValue, isValid)
    return isValid
  }

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    const newValue = e.target.value
    setValue(newValue)
    validateInput(newValue)
  }

  const handleSelectChange = (newValue: string) => {
    setValue(newValue)
    validateInput(newValue)
  }

  const isDisabled = typeof columnMeta.disabled === 'function' ? columnMeta.disabled(row) : columnMeta.disabled

  const renderInputField = () => {
    switch (columnMeta.type) {
      case 'select':
        return (
          <div className="w-full">
            <Select onValueChange={handleSelectChange} defaultValue={initValue}>
              <SelectTrigger
                data-error={!!valMsg}
                title={valMsg}
                disabled={isDisabled}
                className={cn(
                  'w-full data-[error=true]:text-destructive data-[error=true]:ring-destructive data-[error=true]:border-none'
                )}
              >
                <SelectValue placeholder="Select an option" />
              </SelectTrigger>
              <SelectContent>
                <SelectGroup>
                  {columnMeta.options!.map((option: Option) => (
                    <SelectItem key={option.value} value={option.value}>
                      {option.label}
                    </SelectItem>
                  ))}
                </SelectGroup>
              </SelectContent>
            </Select>
            {valMsg && <p className="text-[10px] mt-0.5 text-destructive">{valMsg}</p>}
          </div>
        )

      default:
        return (
          <div className="w-full">
            <Input
              className={cn(
                'h-9 data-[error=true]:text-destructive data-[error=true]:ring-destructive data-[error=true]:border-none'
              )}
              value={value}
              data-error={!!valMsg}
              title={valMsg}
              disabled={isDisabled}
              onChange={handleChange}
              type={columnMeta.type || 'text'}
            />
          </div>
        )
    }
  }

  if (!tableMeta.editedRows[row.id]) {
    return <span className="w-auto">{value}</span>
  }

  return renderInputField()
}

import { MouseEvent, useEffect, useCallback } from 'react'
import { Button } from '@/components/ui/button'
import { Check, Pencil, Trash, X } from 'lucide-react'
import { Row, Table } from '@tanstack/react-table'
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger
} from '@/components/ui/alert-dialog'

interface EditedCellProps<TData> {
  title: string
  row: Row<TData>
  table: Table<TData>
}

export function EditedCell<TData>({ row, table, title }: EditedCellProps<TData>) {
  const meta = table.options.meta

  if (!meta || !meta.validRows || !meta.editedRows) {
    throw new Error('Table meta is not defined')
  }

  const validRow = meta.validRows[row.id]
  const removeRow = () => {
    meta.removeRow!(row.index)
  }


  const disableSubmit = validRow ? Object.values(validRow).some((item) => !item) : false

  const handleAction = useCallback(
    (action: 'edit' | 'cancel' | 'done') => {
      meta.setEditedRows!((old: Record<string, boolean>) => ({
        ...old,
        [row.id]: action === 'edit' ? true : false
      }))

      if (action !== 'edit') {
        if (action === 'cancel') {
          meta.revertData!(row.index)
        } else {
          meta.updateRow!(row.index)
        }
      }
    },
    [row.id, row.index, meta]
  )

  const setEditedRows = useCallback(
    (e: MouseEvent<HTMLButtonElement>) => {
      const action = e.currentTarget.name as 'edit' | 'cancel' | 'done'
      handleAction(action)
    },
    [handleAction]
  )

  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      if (event.key === 'Enter') {
        event.preventDefault()
        if (meta.editedRows && meta.editedRows[row.id] && !disableSubmit) {
          handleAction('done')
        }
      }
    }

    document.addEventListener('keydown', handleKeyDown)

    return () => {
      document.removeEventListener('keydown', handleKeyDown)
    }
  }, [handleAction, row.id, meta.editedRows, disableSubmit])

  return meta.editedRows[row.id] ? (
    <div className="flex items-center space-x-0.5">
      <Button title="Cancel" variant={'ghost'} size={'sm'} onClick={setEditedRows} name="cancel">
        <X className='text-red-600' size={16} />
      </Button>

      <Button
        size={'sm'}
        variant={'ghost'}
        onClick={setEditedRows}
        name="done"
        title="Done"
        disabled={disableSubmit}
        className={disableSubmit ? 'cursor-not-allowed' : ''}
      >
        <Check className='text-green-600' size={16} />
      </Button>
    </div>
  ) : (
    <div className="flex items-center space-x-0.5">
      <Button title="Edit" variant={'ghost'} size={'sm'} onClick={setEditedRows} name="edit">
        <Pencil size={16} />
      </Button>

      <AlertDialog>
        <AlertDialogTrigger title="Delete" asChild>
          <Button size={'sm'} variant={'ghost'} name="delete">
            <Trash className="text-red-600" size={16} />
          </Button>
        </AlertDialogTrigger>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Apakah Kamu yakin?</AlertDialogTitle>
            <AlertDialogDescription>
              Menghapus data <span className="font-bold">{title}</span>?.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Cancel</AlertDialogCancel>
            <AlertDialogAction onClick={removeRow}>Continue</AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  )
}
